import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import Home from "./Home";
import Blog from "./Blog";
import * as serviceWorker from "./serviceWorker";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

// import {IndexRoute} from 'react-router';

import {
  Button,
  Navbar,
  Nav,
  Form,
  FormControl,
  InputGroup,
  ButtonGroup,
} from "react-bootstrap";

import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import ScaleText from "react-scale-text";


ReactDOM.render(
  <Router>
    <Navbar bg="clear" variant="light" sticky="top" expand='true'>
      <Navbar.Brand className="Navbar-brand">
        <Link to="/home" style={{ color: "black" }}>
        <span class="full">Scova Foundation</span>
<span class="short">Scova Fdn.</span>
        </Link>
      </Navbar.Brand>


      <span class="full">
      <ButtonGroup aria-label="Basic example" className="float-sm-right" size="lg">
        <Button href="/book" variant="success">
          Book
        </Button>
        <Button href="/blog" variant="warning">
          Blog
        </Button>
      </ButtonGroup>
      </span>


      <span class="short">
      <ButtonGroup aria-label="Basic example" className="float-sm-right" size="md">
        <Button href="/book" variant="success">
          Book
        </Button>
        <Button href="/blog" variant="warning">
          Blog
        </Button>
      </ButtonGroup>
      </span>


      {/* <Form inline>
        <InputGroup className="mr-3">
          <FormControl
            placeholder="Search Scova"
            aria-label="Recipient's username" 
            aria-describedby="basic-addon2"
          />
          <InputGroup.Append>
            <Button variant="outline-secondary">
              <FontAwesomeIcon icon={faSearch} color="black" />
            </Button>
          </InputGroup.Append>
        </InputGroup>
      </Form> */}
    </Navbar>

    {/* <IndexRoute component={Home} /> */}

    <Route exact path="/" component={Home} />
    <Route path="/home">
      {" "}
      {/* {"/" | "/home"}> */}
      <Home />
    </Route>
    <Route path="/book">
      {" "}
      {/* {"/" | "/home"}> */}
      <App />
    </Route>
    <Route path="/blog">
      <Blog />
    </Route>
  </Router>,
  document.getElementById("root")
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
